mapper.select:
SELECT SUM(i.count)
FROM i_orderitem i,i_order o,i_food f
WHERE i.oid = o.oid
AND i.fid = f.id
AND f.foodName = #{foodName}
AND o.ordertime >=#{startTime}
AND o.ordertime <=#{endTime}
GROUP BY f.foodName

SELECT SUM(i.total)
FROM i_orderitem i,i_order o,i_food f
WHERE i.oid = o.oid
AND i.fid = f.id
AND f.foodName = #{foodName}
AND o.ordertime >=#{startTime}
AND o.ordertime <=#{endTime}
GROUP BY f.foodName

SELECT SUM(i.count)
FROM i_orderitem i,i_order o,i_food f
WHERE i.oid = o.oid
AND i.fid = f.id
AND o.ordertime >=#{startTime}
AND o.ordertime <=#{endTime}

SELECT SUM(i.total)
FROM i_orderitem i,i_order o,i_food f
WHERE i.oid = o.oid
AND i.fid = f.id
AND o.ordertime >=#{startTime}
AND o.ordertime <=#{endTime}

SELECT SUM(i.total) money
FROM i_orderitem i,i_order o,i_food f,i_foodtype t
WHERE i.oid = o.oid
AND i.fid = f.id
AND f.typeid = t.id
AND t.typeName = #{typeName}
AND o.ordertime >=#{startTime}
AND o.ordertime <=#{endTime}
GROUP BY t.typeName
